/*
 * super fast hash, based on Paul Hsieh's function
 * http://www.azillionmonkeys.com/qed/hash.html
 */
#include <stdint.h>
 #undef get16bits
 #if defined(__GNUC__) && defined(__i386__)
	#define get16bits(d) (*((const uint16_t *) (d)))
 #else
	#define get16bits(d) ((((uint32_t)(((const uint8_t *)(d))[1])) << 8)\
                              +(uint32_t)(((const uint8_t *)(d))[0]) )
 #endif

void
d_sfh(uchar *s, int len)
{	uint32_t h = len, tmp;
	int rem;

	rem = len & 3;
	len >>= 2;

	for ( ; len > 0; len--)
	{	h  += get16bits(s);
        	tmp = (get16bits(s+2) << 11) ^ h;
        	h   = (h << 16) ^ tmp;
        	s  += 2*sizeof(uint16_t);
		h  += h >> 11;
	}
	switch (rem) {
	case 3: h += get16bits(s);
		h ^= h << 16;
		h ^= s[sizeof(uint16_t)] << 18;
		h += h >> 11;
		break;
	case 2: h += get16bits(s);
		h ^= h << 11;
		h += h >> 17;
		break;
	case 1: h += *s;
		h ^= h << 10;
		h += h >> 1;
		break;
	}
	h ^= h << 3;
	h += h >> 5;
	h ^= h << 4;
	h += h >> 17;
	h ^= h << 25;
	h += h >> 6;

	K1 = h;
}

#if WS>4
/* 64-bit Jenkins hash, 1997
 * http://burtleburtle.net/bob/c/lookup8.c
 */
#define mix(a,b,c) \
{ a -= b; a -= c; a ^= (c>>43); \
  b -= c; b -= a; b ^= (a<<9);  \
  c -= a; c -= b; c ^= (b>>8);  \
  a -= b; a -= c; a ^= (c>>38); \
  b -= c; b -= a; b ^= (a<<23); \
  c -= a; c -= b; c ^= (b>>5);  \
  a -= b; a -= c; a ^= (c>>35); \
  b -= c; b -= a; b ^= (a<<49); \
  c -= a; c -= b; c ^= (b>>11); \
  a -= b; a -= c; a ^= (c>>12); \
  b -= c; b -= a; b ^= (a<<18); \
  c -= a; c -= b; c ^= (b>>22); \
}
#else
/* 32-bit Jenkins hash, 2006
 * http://burtleburtle.net/bob/c/lookup3.c
 */
#define rot(x,k)	(((x)<<(k))|((x)>>(32-(k))))

#define mix(a,b,c) \
{ a -= c;  a ^= rot(c, 4);  c += b; \
  b -= a;  b ^= rot(a, 6);  a += c; \
  c -= b;  c ^= rot(b, 8);  b += a; \
  a -= c;  a ^= rot(c,16);  c += b; \
  b -= a;  b ^= rot(a,19);  a += c; \
  c -= b;  c ^= rot(b, 4);  b += a; \
}

#define final(a,b,c) \
{ c ^= b; c -= rot(b,14); \
  a ^= c; a -= rot(c,11); \
  b ^= a; b -= rot(a,25); \
  c ^= b; c -= rot(b,16); \
  a ^= c; a -= rot(c,4);  \
  b ^= a; b -= rot(a,14); \
  c ^= b; c -= rot(b,24); \
}
#endif

void
d_hash(uchar *kb, int nbytes)
{	uint8_t  *bp;
#if WS>4
	uint64_t a = 0, b, c, n;
	const uint64_t *k = (uint64_t *) kb;
#else
	uint32_t a = 0, b, c, n;
	const uint32_t *k = (uint32_t *) kb;
#endif
	n = nbytes/WS;	/* nr of words */
	/* extend to multiple of words, if needed */
	a = WS - (nbytes % WS);
	if (a > 0 && a < WS)
	{	n++;
		bp = kb + nbytes;
		switch (a) {
#if WS>4
		case 7: *bp++ = 0; /* fall thru */
		case 6: *bp++ = 0; /* fall thru */
		case 5: *bp++ = 0; /* fall thru */
		case 4: *bp++ = 0; /* fall thru */
#endif
		case 3: *bp++ = 0; /* fall thru */
		case 2: *bp++ = 0; /* fall thru */
		case 1: *bp   = 0;
		case 0: break;
	}	}
#if WS>4
	b = HASH_CONST[HASH_NR];
	c = 0x9e3779b97f4a7c13LL; /* arbitrary value */
	while (n >= 3)
	{	a += k[0];
		b += k[1];
		c += k[2];
		mix(a,b,c);
		n -= 3;
		k += 3;
	}
	c += (((uint64_t) nbytes)<<3);
	switch (n) {
	case 2: b += k[1];
	case 1: a += k[0];
	case 0: break;
	}
	mix(a,b,c);
#else
	a = c = 0xdeadbeef + (n<<2);
	b = HASH_CONST[HASH_NR];
	while (n > 3)
	{	a += k[0];
		b += k[1];
		c += k[2];
		mix(a,b,c);
		n -= 3;
		k += 3;
	}
	switch (n) { 
	case 3: c += k[2];
	case 2: b += k[1];
	case 1: a += k[0];
		final(a,b,c);
	case 0: break;
	}
#endif
	j1_spin = c&nmask; j3_spin = a&7; /* 1st bit */
	j2_spin = b&nmask; j4_spin = (a>>3)&7; /* 2nd bit */
	K1 = c; K2 = b;
}

#if defined(MURMUR) && (WS==8)
/* public-domain, 64-bit MurmurHash3, by Austin Appleby */
/*  https://code.google.com/p/smhasher/wiki/MurmurHash3 */
void
m_hash(uchar *v, int len)
{	uint8_t *bp, *data = (uint8_t*) v;
	int i, nblocks = len / 16;

	uint64_t h1 = HASH_CONST[HASH_NR];
	uint64_t h2 = 0x9e3779b97f4a7c13LL;

	uint64_t c1 = 0x87c37b91114253d5;
	uint64_t c2 = 0x4cf5ad432745937f;

	uint64_t *blocks = (uint64_t *)(data);

	/* guarantee a multiple of 16 bytes */
	i = 16 - (len % 16);
	if (i > 0 && i < 16)
	{	nblocks++;
		bp = v + len;
		switch (i) {
		case 15: *bp++ = 0; /* fall thru */
		case 14: *bp++ = 0;
		case 13: *bp++ = 0;
		case 12: *bp++ = 0;
		case 11: *bp++ = 0;
		case 10: *bp++ = 0;
		case  9: *bp++ = 0;
		case  8: *bp++ = 0;
		case  7: *bp++ = 0;
		case  6: *bp++ = 0;
		case  5: *bp++ = 0;
		case  4: *bp++ = 0;
		case  3: *bp++ = 0;
		case  2: *bp++ = 0;
		case  1: *bp   = 0;
		case  0: break;
       }       }

	for (i = 0; i < nblocks; i++)
	{	uint64_t k1 = blocks[i*2];
		uint64_t k2 = blocks[i*2+1];

		k1 *= c1;
		k1 = (k1 << 31) | (k1 >> 33);
		k1 *= c2;
		h1 ^= k1;

		h1 = (h1 << 27) | (h1 >> 37);
		h1 += h2;
		h1 = h1 * 5 + 0x52dce729;

		k2 *= c2;
		k2 = (k2 << 33) | (k2 >> 31);
		k2 *= c1;
		h2 ^= k2;

		h2 = (h2 << 31) | (h2 >> 33);
		h2 += h1;
		h2 = h2 * 5 + 0x38495ab5;
	}

	uint8_t *tail = (uint8_t*)(data + (nblocks * 16));

	uint64_t k1 = 0;
	uint64_t k2 = 0;

	switch(len & 15) {
	case 15: k2 ^= ((uint64_t) tail[14]) << 48; break;
	case 14: k2 ^= ((uint64_t) tail[13]) << 40; break;
	case 13: k2 ^= ((uint64_t) tail[12]) << 32; break;
	case 12: k2 ^= ((uint64_t) tail[11]) << 24; break;
	case 11: k2 ^= ((uint64_t) tail[10]) << 16; break;
	case 10: k2 ^= ((uint64_t) tail[ 9]) << 8; break;
	case  9: k2 ^= ((uint64_t) tail[ 8]) << 0; break;
		 k2 *= c2;
		 k2 = (k2 << 33) | (k2 >> 31);
		 k2 *= c1;
		 h2 ^= k2; break;
	case  8: k1 ^= ((uint64_t) tail[7]) << 56; break;
	case  7: k1 ^= ((uint64_t) tail[6]) << 48; break;
	case  6: k1 ^= ((uint64_t) tail[5]) << 40; break;
	case  5: k1 ^= ((uint64_t) tail[4]) << 32; break;
	case  4: k1 ^= ((uint64_t) tail[3]) << 24; break;
	case  3: k1 ^= ((uint64_t) tail[2]) << 16; break;
	case  2: k1 ^= ((uint64_t) tail[1]) << 8; break;
	case  1: k1 ^= ((uint64_t) tail[0]) << 0; break;
		 k1 *= c1;
		 k1 = (k1 << 31) | (k1 >> 33);
		 k1 *= c2;
		 h1 ^= k1;
	};

	h1 ^= len; h2 ^= len;
	h1 += h2;
	h2 += h1;
	h1 ^= h1 >> 33;
	h1 *= 0xff51afd7ed558ccd;
	h1 ^= h1 >> 33;
	h1 *= 0xc4ceb9fe1a85ec53;
	h1 ^= h1 >> 33;
	h2 ^= h2 >> 33;
	h2 *= 0xff51afd7ed558ccd;
	h2 ^= h2 >> 33;
	h2 *= 0xc4ceb9fe1a85ec53;
	h2 ^= h2 >> 33;
	h1 += h2;
	h2 += h1;

	j1_spin = h1&nmask; j3_spin = (h1>>48)&7;
	j2_spin = h2&nmask; j4_spin = (h2>>48)&7;
	K1 = h1; K2 = h2;
}
#endif

