int	/* to avoid having to include <math.h> and compile with -lm */
blog2(int n)	/* n >= 1 */
{	int m=1, r=2;
	if (n == 1) { return 0; }
	if (n == 2) { return 1; }
	while (n > r) { m++; r *= 2; }
	return m;
}
