#ifdef TRIX
void
sv_populate(void)
{	int i, cnt = 0;
	TRIX_v6 **base = processes;
	int bound = now._nr_pr; /* MAXPROC+1; */
#ifdef V_TRIX
	printf("%4d: sv_populate\n", depth);
#endif
again:
	for (i = 0; i < bound; i++)
	{	if (base[i] != NULL)
		{	H_el *tmp;
			int m, n; uchar *v;
#ifndef BFS
			if (base[i]->modified == 0)
			{	cnt++;
	#ifdef V_TRIX
				printf("%4d: %s %d not modified\n",
				depth, (base == processes)?"proc":"chan", i);
	#endif
				continue;
			}
	#ifndef V_MOD
			base[i]->modified = 0;
	#endif
#endif
#ifdef TRIX_RIX
			if (base == processes)
			{	((P0 *)pptr(i))->_pid = 0;
			}
#endif
			n = base[i]->psize;
			v = base[i]->body;
			s_hash(v, n); /* sets j1_spin */
			tmp = H_tab[j1_spin];
			if (!tmp)	/* new */
			{	tmp = grab_state(n);
				H_tab[j1_spin] = tmp;
				m = 1; /* non-zero */
			} else
			{  H_el *ntmp, *olst = (H_el *) 0;
			   for (;; hcmp++, olst = tmp, tmp = tmp->nxt)
			   { 	m = memcmp(((char *)&(tmp->state)), v, n);
				if (m == 0)	/* match */
				{	break;
				} else if (m < 0) /* insert */
				{	ntmp = grab_state(n);
					ntmp->nxt = tmp;
					if (!olst)
						H_tab[j1_spin] = ntmp;
					else
						olst->nxt = ntmp;
					tmp = ntmp;
					break;
				} else if (!tmp->nxt)	/* append */
				{	tmp->nxt = grab_state(n);
					tmp = tmp->nxt;
					break;
			}  }	}
			if (m != 0)
			{	memcpy((char *)&(tmp->state), v, n);
#if defined(AUTO_RESIZE) && !defined(BITSTATE)
				tmp->m_K1 = K1; /* set via s_hash */
#endif
				if (verbose)
				{	if (base == processes)
					{	_p_count[i]++;
					} else
					{	_c_count[i]++;
			}	}	}
			now._ids_[cnt++] = (char *)&(tmp->state);
#ifdef TRIX_RIX
			if (base == processes)
			{	((P0 *)pptr(i))->_pid = i;
				if (BASE > 0 && i > 0)
				{	((P0 *)pptr(i))->_pid -= BASE;
			}	}
#endif
	}	}
	/* do the same for all channels */
	if (base == processes)
	{	base = channels;
		bound = now._nr_qs; /* MAXQ+1; */
		goto again;
	}
}
#endif

