void
printm(int x, char *s)
{
	if (!s) { s = "_unnamed_"; }
	if (strcmp(s, "_unnamed_") == 0)
	switch (x) {
	default: Printf("%d", x); return;
	}
	if (strcmp(s, "State") == 0)
	switch (x) {
	case 1: Printf("crashed"); return;
	case 2: Printf("follower"); return;
	case 3: Printf("candidate"); return;
	case 4: Printf("leader"); return;
	default: Printf("%d", x); return;
	}
	if (strcmp(s, "MessageType") == 0)
	switch (x) {
	case 1: Printf("REQUEST_VOTE_RESPONSE"); return;
	case 2: Printf("REQUEST_VOTE"); return;
	case 3: Printf("APPEND_ENTRY_RESPONSE"); return;
	case 4: Printf("APPEND_ENTRY"); return;
	default: Printf("%d", x); return;
	}
}
